import NextImage from 'next/image'

import ReactMarkdown from 'react-markdown'

import styles from './BlogPost.module.scss'

interface Props {
  title: string
  content: string
  date: string
  image: string
}

const BlogPost = ({ title, content, date, image }: Props) => {
  return (
    <div className={styles.blog}>
      <h1 className={styles.blog__title}>{title}</h1>
      <span className={styles.blog__date}>{date}</span>
      <div className={styles.blog__image}>
        <NextImage
          src={image}
          width='900'
          height='600'
          placeholder='blur'
          blurDataURL='data:image/webp;base64,'
        />
      </div>
      <ReactMarkdown className={styles.blog__content}>{content}</ReactMarkdown>
    </div>
  )
}

export default BlogPost
